script:
console.log('Load and process', this.action, this.variable);

const recreateCharts = await API.require('vh/eln/sample/ml/recreateCharts');

recreateCharts(this.variable);

if (! this.button) this.button='default';

var layers={
    'default': {
        layer:'Default layer'
    },
    'deconvolution': {
        layer:'Deconvolution Admin'
    },
}

if (layers[this.button]) {
    for (var key of Object.keys(layers)) {
        this.enableButton(key);
    }
    API.switchToLayer(layers[this.button].layer);
    if (layers[this.button].callback) {
        layers[this.button].callback();
    }
    this.disableButton(this.button);
}

if (this.button) { // just used for debug
    this.action={
        name:this.button
    }
}

const UI=await API.require('src/util/ui');
let currentAnalysisResults=API.getData('currentAnalysisResults');

let userAnalysisResults=API.cache("userAnalysisResults");

switch (this.action.name) {
    case 'saveResultTemplate': {
        let value = (currentAnalysisResults) ? currentAnalysisResults.resurrect().$id[3] : '';
        let name=await UI.enterValue({
            label: 'Enter the preference name',
            buttonLabel: 'Save',
            value
        });
        if (!name) return;
        await userAnalysisResults.saveTemplate(name, API.getData('preferences').resurrect(), {
            preferences: API.getData('preferences').resurrect(),
            description: API.getData('description').resurrect(),
            spectraInDataset: API.getData('spectraInDataset').resurrect()
        });
        API.doAction('refreshResults');
        break;
    }
    case 'refreshResults':
        console.log('refresh');
        await userAnalysisResults.refresh();
        break;
    case 'saveAsAttachment': {
        let json = JSON.stringify(getData());
        let cdb = new CDB();
        let data = new Blob([json], {type: 'application/json'});
        cdb.upload({
            data,
            name: 'upload/example.json'
        });
    }
    case 'deleteResultTemplate': {
        let result=await UI.confirm('Are you sure you want to delete this template ?', 'Delete', 'Cancel', {});
        if (! result) return;
        await userAnalysisResults.delete(this.action.value);
        API.doAction('refreshResults');
        break;
    }
    case 'loadResultTemplate': {
        let result = await userAnalysisResults.loadResult(this.action.value);

        let newPreferences=result.preferences;
        let preferences=API.getData('preferences');
        copyPreferences(newPreferences, preferences);
        preferences.triggerChange();
        API.getData('twigPreferences').triggerChange();
        API.createData('description', result.description);
        API.createData('spectraInDataset', result.spectraInDataset);
        break;
    }
    case 'selectResultTemplate': {
        let templates=API.getData('analysisTemplates');
        if (! templates) {
            UI.showNotification('Only available when linked to a database','warning');
            return;
        }
        let result = await UI.form(`
            <div style="zoom: 1.5;">
                <form>
                    <p style="padding-bottom: 10px">
                    Reload one of the previously saved preferences:
                    </p>
                    <select name='template'>
                        ${templates.map( template => {
                            let key=String(template.$id[3]);
                            let _id=String(template._id);
                            return `
                                <option value="${_id}">${key}</option>
                            `
                        })}

                    </select>
                    <input type="submit" value="Load preferences"/>
                </form>
            </div>
        `)

        if (result.template) {
            API.doAction('loadResultTemplate', {_id:result.template})
        }
        break;
    }
}

function setData(result) {
        API.createData('description', result.description);
        API.createData('preferences', result.preferences);

        let ranges=API.getData('ranges'); // ranges are manage by rangeManager
        ranges.length=0;
        result.ranges.forEach( (range) => ranges.push(range));
        ranges.triggerChange();
        window.setTimeout( () => {
            API.getData('twigPreferences').triggerChange();
        },100)

}

function getData() {
    return {
        ranges: API.getData('ranges').resurrect(),
        preferences: API.getData('preferences').resurrect(),
        description: API.getData('description').resurrect(),
    }
}

function copyPreferences(newPreferences, preferences) {
    Object.keys(newPreferences).forEach( key => {
        preferences[key]=JSON.parse(JSON.stringify(newPreferences[key]))
    })
}

let value=this.action.value;

switch (this.action.name) {
    case 'trackClicked': {
        if (value.event.altKey && value.event.shiftKey) {
            let key=Object.keys(value.data)[0];
            let index=value.data[key].indexClosest;
            let preferences=API.getData('preferences');
            preferences.setChildSync(['display','correlationIndex'],Number(index));
        }
        break;
    }
    case 'trackMove': {
        let displayPreferences = API.getData('preferences').display.resurrect();
        const trackMove = this.action.value.data.chart;
        if (! trackMove || ! displayPreferences.trackingInfo || displayPreferences.original==='true') {
            API.createData('trackAnnotations', []);
            return;
        }
        const spectraProcessor=API.cache('spectraProcessor');
        const annotations=spectraProcessor.getTrackAnnotation(trackMove.indexBefore)
        API.createData('trackAnnotations', annotations)
        break;
    }
    case 'SetTarget':
        const preferences=API.getData('preferences');
        preferences.setChildSync(['deconvolution','targetID'],this.action.value.id)
        let spectraInDataset=API.getData('spectraInDataset');
        let targets = spectraInDataset.filter( spectrum => String(this.action.value.id) === String(spectrum.id));
        targets[0].reference=false;
        spectraInDataset.triggerChange()
        break;
    case 'downloadNormalizedMatrix':
        const spectraProcessor = API.cache('spectraProcessor');
        let tsv=spectraProcessor.getNormalizedTSV();
        let fileSaver=await API.require('file-saver');
        var blob = new Blob([tsv], {type: "text/tab-separated-values;charset=utf-8"});
        fileSaver( blob, "data.tsv");
        break;
    case 'setAsReference': {
        let spectraInDataset=API.getData('spectraInDataset');
        let currentlySelectedSpectra=API.getData('currentlySelectedSpectra');
        for (let spectrum of currentlySelectedSpectra) {
            spectraInDataset.filter( (spec) => String(spec.id)===String(spectrum.id))[0].reference=true;
        }
        spectraInDataset.triggerChange();
        break;        
    }

    case 'unsetAsReference': {
        let spectraInDataset=API.getData('spectraInDataset');
        let currentlySelectedSpectra=API.getData('currentlySelectedSpectra');
        for (let spectrum of currentlySelectedSpectra) {
            spectraInDataset.filter( (spec) => String(spec.id)===String(spectrum.id))[0].reference=false;
        }
        spectraInDataset.triggerChange();
        break;
    }
    default:
}

const {Matrix, Array:ArrayUtil, SVD, FCNNLS} = ML;

let preferences = API.getData('preferences').resurrect();

let spectraProcessor=API.cache('spectraProcessor');

let referencesID = API.getData('spectraInDataset').resurrect().filter( spectrum => spectrum.reference).map(spectrum => spectrum.id);
let targetID = preferences.deconvolution.targetID;

if (! targetID || ! spectraProcessor.getSpectrum(targetID)) {
    UI.showNotification('You need to specify a target spectrum', 'warning');
    return;
}

let data = spectraProcessor.getNormalizedData({ids: referencesID});
let A = new Matrix(data.matrix);
let At = A.transpose();
let b = spectraProcessor.getSpectrum(targetID).normalized.y;

var result;
console.log(preferences.deconvolution.method);
switch (preferences.deconvolution.method.toLowerCase()) {
    case 'svd':
        var svd = new SVD(At);
        var x = svd.solve(Matrix.columnVector(b));
        result=x.to1DArray();
        break;
    case 'fcnnls':
        result = FCNNLS.fcnnlsVector(At, b, {maxIterations:1000});
        console.log(result);
        break;
    default:
        throw new Error('Unknown method: ',preferences.deconvolution.method)
}

let reconstructed={
    x: data.x,
    y: (new Matrix([result])).mmul(A).to1DArray()
};
API.createData('reconstructed', reconstructed);

let error = {
    abs:0,
    eucledian: 0
}
var cumulatedErrorEucledian=0;
for (var i=0; i<reconstructed.y.length; i++) {
    error.abs+=Math.abs(reconstructed.y[i]-b[i]);
    error.eucledian+=(reconstructed.y[i]-b[i])**2;
}
error.eucledian=Math.sqrt(error.eucledian);

let spectraInDataset=API.getData('spectraInDataset');


let normed=ML.Array.normed(result);


console.log(spectraInDataset)

for (let i=0; i<spectraInDataset.length; i++) {
    let spectrum=spectraInDataset[i];
    let id=String(spectrum.id);
    let index=referencesID.indexOf(id);
    if (index>=0) {
        spectrum.quantity = normed[index];
    } else {
        spectrum.quantity = 0;
    }
}
console.log('TRIGGER')
spectraInDataset.triggerChange();

API.createData('error', error);

const twigPreferences=`
${Prefs.style}


<div id='extendedForm'>
    <form>

    ${Prefs.dataNormalization}


    <h1>Deconvolution parameters</h1>

    Method:
    <select name="deconvolution.method">
        <option value="svd">Singular value decomposition</option>
        <option value="fcnnls">Fast Combinatorial Non-negative Least Squares</option>
    </select><br>

    ${Prefs.spectraDisplay}

<!--
    <center>
         Auto-refresh <input type="checkbox" name="display.autorefresh" checked> 
        or <input type="submit" value="Update data and chart">
    </center>
-->
    </form>
</div>


<script>
    require(['vh/util/twigAdvancedForm'], function(AF) {
        AF('extendedForm', {debug:false});
    });
</script>
`;

API.createData('twigPreferences', twigPreferences);

const [
    Roc,
    loadTwig,
    UserAnalysisResults,
    Toc,
    SpectraDataSet,
    libs,
] = await API.require([
    'vh/rest-on-couch/Roc',
    'vh/eln/templates/loadTwig',
    'vh/rest-on-couch/UserAnalysisResults',
    'vh/eln/Toc',
    'vh/eln/sample/ml/SpectraDataSet',
    'vh/eln/libs',
]);

const SpectraProcessor = (await API.require('SpectraProcessor')).SpectraProcessor;
API.cache('spectraProcessor', new SpectraProcessor({}));

if((typeof IframeBridge)!=='undefined') {
    IframeBridge.onMessage(onMessage);
    IframeBridge.ready();
}

async function onMessage(data) {
    if(data.type === 'tab.data') {
        var couchDB = data.message.couchDB;
        var uuid = data.message.uuid;
        API.cache('couchDB', couchDB);
        API.cache('uuid', uuid);
        var roc = new Roc(couchDB);
        API.cache('roc', roc);

        var userAnalysisResults = new UserAnalysisResults(roc, data.message.uuid);
        API.cache("userAnalysisResults", userAnalysisResults);
        userAnalysisResults.refresh();
        API.createData('description', '');



        const sampleToc = new Toc(roc);
        sampleToc.initializeGroupForm();
        API.cache('sampleToc', sampleToc);

        const spectraDataSet = new SpectraDataSet(roc, sampleToc, {
            defaultAttributes: {
                reference: true,
                quantity: '',
            }
        });
        spectraDataSet.initializeAnalysis();
        API.cache('spectraDataSet', spectraDataSet);

    }
}

API.createData('spectraInDataset',[]);

API.createData('preferences', {
    data: {keep: true},
    display: {
        selection: 'selected',
        original: false,
        trackingInfo: false,
       correlationIndex: false,
        boxplot: false,
        boxplotOptions: {
